import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  TouchableOpacity,
  SafeAreaView,
  ScrollView,
  ActivityIndicator,
} from "react-native";
import { useNavigation } from "@react-navigation/native";
import { StackNavigationProp } from "@react-navigation/stack";
import { RootStackParamList } from "../types/navigation";
import { getTodayIssueNews, getPopularKeywords } from "../services/newsService";

// 간단한 날짜 포맷 함수
const formatDate = (date: Date): string => {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  return `${year}-${month}-${day}`;
};

type HomeScreenNavigationProp = StackNavigationProp<RootStackParamList, "Home">;

const HomeScreen = () => {
  const navigation = useNavigation<HomeScreenNavigationProp>();
  const [searchKeyword, setSearchKeyword] = useState("");
  const [todayIssues, setTodayIssues] = useState<any[]>([]);
  const [popularKeywords, setPopularKeywords] = useState<string[]>([]);
  const [isIssuesLoading, setIsIssuesLoading] = useState(true);
  const [isKeywordsLoading, setIsKeywordsLoading] = useState(true);

  useEffect(() => {
    loadTodayIssues();
    loadPopularKeywords();
  }, []);

  const loadTodayIssues = async () => {
    try {
      setIsIssuesLoading(true);
      const issues = await getTodayIssueNews(10);
      setTodayIssues(issues);
    } catch (error) {
      console.error("오늘의 이슈 로드 오류:", error);
    } finally {
      setIsIssuesLoading(false);
    }
  };

  const loadPopularKeywords = async () => {
    try {
      setIsKeywordsLoading(true);
      const keywords = await getPopularKeywords(6); // 6개 키워드 가져오기
      setPopularKeywords(keywords);
    } catch (error) {
      console.error("인기 키워드 로드 오류:", error);
      // 오류 발생 시 기본 키워드 설정
      setPopularKeywords(["경제", "주식", "부동산", "금융", "기업", "국제"]);
    } finally {
      setIsKeywordsLoading(false);
    }
  };

  const handleSearch = () => {
    if (searchKeyword.trim()) {
      navigation.navigate("StoryResult", { keyword: searchKeyword });
    }
  };

  const goToAspenHome = () => {
    navigation.navigate("AspenHome");
  };

  const renderIssueItem = ({ item }) => (
    <TouchableOpacity
      style={styles.issueCard}
      onPress={() => navigateToIssueTimeline(item)}
    >
      <View style={styles.issueCardContent}>
        <Text style={styles.issueTitle} numberOfLines={2}>
          {item.topic || "이슈 제목"}
        </Text>
        <Text style={styles.issueKeywords} numberOfLines={1}>
          {(item.topic_keyword || "").split(",").slice(0, 3).join(", ")}
        </Text>
        <Text style={styles.issueDate}>{formatDate(new Date())}</Text>
      </View>
    </TouchableOpacity>
  );

  const navigateToIssueTimeline = (issue) => {
    navigation.navigate("IssueTimeline", {
      issue: issue,
      keyword: issue.topic_keyword ? issue.topic_keyword.split(",")[0] : "",
    });
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView>
        <View style={styles.header}>
          <Text
            style={[
              styles.title,
              { fontFamily: undefined, fontWeight: "bold" },
            ]}
          >
            스토리 in 서울경제
          </Text>
          <Text style={[styles.subtitle, { fontFamily: undefined }]}>
            당신이 궁금한 모든 이야기
          </Text>
        </View>

        <View style={styles.searchContainer}>
          <TextInput
            style={[styles.searchInput, { fontFamily: undefined }]}
            placeholder="키워드를 입력하세요"
            value={searchKeyword}
            onChangeText={setSearchKeyword}
            onSubmitEditing={handleSearch}
          />
          <TouchableOpacity style={styles.searchButton} onPress={handleSearch}>
            <Text
              style={[
                styles.searchButtonText,
                { fontFamily: undefined, fontWeight: "bold" },
              ]}
            >
              검색
            </Text>
          </TouchableOpacity>
        </View>

        <View style={styles.recommendContainer}>
          <Text
            style={[
              styles.recommendTitle,
              { fontFamily: undefined, fontWeight: "600" },
            ]}
          >
            인기 키워드
          </Text>
          <View style={styles.keywordContainer}>
            {isKeywordsLoading ? (
              <ActivityIndicator size="small" color="#176FF2" />
            ) : (
              popularKeywords.map((keyword, index) => (
                <TouchableOpacity
                  key={index}
                  style={styles.keywordButton}
                  onPress={() => {
                    setSearchKeyword(keyword);
                    navigation.navigate("StoryResult", {
                      keyword,
                      options: {
                        sortBy: "date", // 최신순 정렬
                      },
                    });
                  }}
                >
                  <Text style={[styles.keywordText, { fontFamily: undefined }]}>
                    {keyword}
                  </Text>
                </TouchableOpacity>
              ))
            )}
          </View>
        </View>

        <View style={styles.featuredContainer}>
          <Text
            style={[
              styles.featuredTitle,
              { fontFamily: undefined, fontWeight: "600" },
            ]}
          >
            오늘의 추천 스토리
          </Text>
          <TouchableOpacity style={styles.featuredCard} onPress={goToAspenHome}>
            <View style={styles.cardContent}>
              <Text
                style={[
                  styles.cardTitle,
                  { fontFamily: undefined, fontWeight: "600" },
                ]}
              >
                Anima로 디자인된 샘플 화면으로 이동
              </Text>
              <Text style={[styles.cardDescription, { fontFamily: undefined }]}>
                Anima에서 변환된 디자인 샘플을 확인해보세요
              </Text>
            </View>
          </TouchableOpacity>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f9f9f9",
  },
  header: {
    padding: 20,
    alignItems: "center",
  },
  title: {
    fontFamily: "Montserrat-Bold",
    fontSize: 24,
    marginBottom: 8,
    color: "#176FF2",
  },
  subtitle: {
    fontFamily: "CircularXX-Medium",
    fontSize: 16,
    color: "#5F5F5F",
  },
  searchContainer: {
    flexDirection: "row",
    paddingHorizontal: 20,
    marginBottom: 20,
  },
  searchInput: {
    flex: 1,
    height: 50,
    backgroundColor: "#ffffff",
    borderRadius: 8,
    paddingHorizontal: 15,
    marginRight: 10,
    fontFamily: "CircularXX-Regular",
    borderWidth: 1,
    borderColor: "#eaeaea",
  },
  searchButton: {
    backgroundColor: "#176FF2",
    borderRadius: 8,
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 20,
  },
  searchButtonText: {
    color: "#ffffff",
    fontFamily: "CircularXX-Bold",
    fontSize: 16,
  },
  recommendContainer: {
    padding: 20,
  },
  recommendTitle: {
    fontFamily: "Montserrat-SemiBold",
    fontSize: 18,
    marginBottom: 15,
    color: "#232323",
  },
  keywordContainer: {
    flexDirection: "row",
    flexWrap: "wrap",
  },
  keywordButton: {
    backgroundColor: "rgba(23,111,242,0.1)",
    paddingHorizontal: 15,
    paddingVertical: 8,
    borderRadius: 20,
    marginRight: 10,
    marginBottom: 10,
  },
  keywordText: {
    fontFamily: "CircularXX-Regular",
    color: "#176FF2",
  },
  featuredContainer: {
    padding: 20,
  },
  featuredTitle: {
    fontFamily: "Montserrat-SemiBold",
    fontSize: 18,
    marginBottom: 15,
    color: "#232323",
  },
  featuredCard: {
    backgroundColor: "#ffffff",
    borderRadius: 12,
    overflow: "hidden",
    marginBottom: 15,
    elevation: 2,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
  },
  cardContent: {
    padding: 15,
  },
  cardTitle: {
    fontFamily: "Montserrat-SemiBold",
    fontSize: 16,
    marginBottom: 8,
    color: "#232323",
  },
  cardDescription: {
    fontFamily: "CircularXX-Regular",
    fontSize: 14,
    color: "#5F5F5F",
    lineHeight: 20,
  },
  issueCard: {
    backgroundColor: "#ffffff",
    borderRadius: 8,
    padding: 10,
    marginBottom: 10,
  },
  issueCardContent: {
    flexDirection: "row",
    alignItems: "center",
  },
  issueTitle: {
    fontFamily: "Montserrat-SemiBold",
    fontSize: 16,
    marginBottom: 2,
    color: "#232323",
  },
  issueKeywords: {
    fontFamily: "CircularXX-Regular",
    fontSize: 14,
    color: "#5F5F5F",
  },
  issueDate: {
    fontFamily: "CircularXX-Regular",
    fontSize: 12,
    color: "#5F5F5F",
  },
});

export default HomeScreen;
