import React, { useEffect, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  SafeAreaView,
} from "react-native";
import { useRoute, RouteProp, useNavigation } from "@react-navigation/native";
import { StackNavigationProp } from "@react-navigation/stack";
import { RootStackParamList } from "../types/navigation";

// 서비스 import (추후 구현)
// import { searchNews } from '../services/newsService';

// 임시 뉴스 데이터 인터페이스
interface NewsItem {
  id: string;
  title: string;
  description: string;
  date: string;
  url: string;
}

type StoryResultScreenRouteProp = RouteProp<RootStackParamList, "StoryResult">;
type StoryResultScreenNavigationProp = StackNavigationProp<
  RootStackParamList,
  "StoryResult"
>;

const StoryResultScreen = () => {
  const route = useRoute<StoryResultScreenRouteProp>();
  const navigation = useNavigation<StoryResultScreenNavigationProp>();
  const { keyword } = route.params;

  const [loading, setLoading] = useState(true);
  const [news, setNews] = useState<NewsItem[]>([]);

  useEffect(() => {
    // 실제 API 연동 시 주석 해제
    // searchNews(keyword).then(data => {
    //   setNews(data);
    //   setLoading(false);
    // }).catch(error => {
    //   console.error(error);
    //   setLoading(false);
    // });

    // 임시 데이터로 대체
    setTimeout(() => {
      setNews([
        {
          id: "1",
          title: `${keyword} 관련 뉴스: 경제 전망 밝아져`,
          description:
            "최근 발표된 경제지표에 따르면 올해 경제성장률은 예상보다 높을 것으로 전망됩니다.",
          date: "2023-07-15",
          url: "https://example.com/news/1",
        },
        {
          id: "2",
          title: `${keyword}에 대한 전문가 의견: "투자 기회 많아"`,
          description:
            "전문가들은 현재 시장 상황이 장기 투자자들에게 좋은 기회라고 말합니다.",
          date: "2023-07-14",
          url: "https://example.com/news/2",
        },
        {
          id: "3",
          title: `정부, ${keyword} 관련 새로운 정책 발표`,
          description:
            "정부가 새로운 경제 활성화 정책을 발표했습니다. 이번 정책은 특히 중소기업에 혜택이 많습니다.",
          date: "2023-07-13",
          url: "https://example.com/news/3",
        },
      ]);
      setLoading(false);
    }, 1500);
  }, [keyword]);

  const renderNewsItem = ({ item }: { item: NewsItem }) => (
    <TouchableOpacity
      style={styles.newsItem}
      onPress={() =>
        navigation.navigate("NewsDetail", { url: item.url, title: item.title })
      }
    >
      <Text style={styles.newsTitle}>{item.title}</Text>
      <Text style={styles.newsDescription}>{item.description}</Text>
      <Text style={styles.newsDate}>{item.date}</Text>
    </TouchableOpacity>
  );

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#176FF2" />
        <Text style={styles.loadingText}>검색 중...</Text>
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <Text style={styles.resultHeader}>
        "{keyword}" 관련 스토리 {news.length}개를 찾았습니다
      </Text>
      <FlatList
        data={news}
        renderItem={renderNewsItem}
        keyExtractor={(item) => item.id}
        contentContainerStyle={styles.newsList}
      />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f9f9f9",
  },
  loadingContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  loadingText: {
    marginTop: 10,
    fontFamily: "CircularXX-Medium",
    color: "#5F5F5F",
  },
  resultHeader: {
    fontFamily: "Montserrat-SemiBold",
    fontSize: 16,
    marginHorizontal: 20,
    marginVertical: 15,
    color: "#232323",
  },
  newsList: {
    paddingHorizontal: 20,
  },
  newsItem: {
    backgroundColor: "#ffffff",
    borderRadius: 12,
    padding: 15,
    marginBottom: 15,
    elevation: 2,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
  },
  newsTitle: {
    fontFamily: "Montserrat-SemiBold",
    fontSize: 16,
    marginBottom: 8,
    color: "#232323",
  },
  newsDescription: {
    fontFamily: "CircularXX-Regular",
    fontSize: 14,
    color: "#5F5F5F",
    marginBottom: 12,
    lineHeight: 20,
  },
  newsDate: {
    fontFamily: "CircularXX-Book",
    fontSize: 12,
    color: "#B8B8B8",
  },
});

export default StoryResultScreen;
