import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  ActivityIndicator,
  SafeAreaView,
} from "react-native";
import { RouteProp } from "@react-navigation/native";
import { StackNavigationProp } from "@react-navigation/stack";
import { RootStackParamList } from "../types/navigation";
import {
  searchNewsByKeyword,
  IssueItem,
  NewsItem,
} from "../services/newsService";

type IssueTimelineRouteProp = RouteProp<RootStackParamList, "IssueTimeline">;
type IssueTimelineNavigationProp = StackNavigationProp<
  RootStackParamList,
  "IssueTimeline"
>;

type Props = {
  route: IssueTimelineRouteProp;
  navigation: IssueTimelineNavigationProp;
};

const IssueTimeline = ({ route, navigation }: Props) => {
  const { issue, keyword } = route.params;
  const [relatedNews, setRelatedNews] = useState<NewsItem[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRelatedNews = async () => {
      try {
        setLoading(true);
        // 이슈와 관련된 뉴스 검색 (키워드 배열에서 첫 번째 키워드 사용)
        const primaryKeyword =
          issue.keywords && issue.keywords.length > 0
            ? issue.keywords[0]
            : keyword;

        const newsResults = await searchNewsByKeyword(primaryKeyword, 10);
        setRelatedNews(newsResults);
      } catch (error) {
        console.error("관련 뉴스 로딩 오류:", error);
        setRelatedNews([]);
      } finally {
        setLoading(false);
      }
    };

    fetchRelatedNews();
  }, [issue, keyword]);

  const formatDate = (dateString: string) => {
    if (!dateString) return "";

    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");

    return `${year}.${month}.${day}`;
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView>
        <View style={styles.header}>
          <TouchableOpacity
            style={styles.backButton}
            onPress={() => navigation.goBack()}
          >
            <Text style={styles.backButtonText}>← 뒤로</Text>
          </TouchableOpacity>
          <Text style={styles.headerTitle}>이슈 상세정보</Text>
        </View>

        <View style={styles.issueContainer}>
          <Text style={styles.issueTitle}>{issue.topic}</Text>

          <View style={styles.issueMetaContainer}>
            <View style={styles.rankBadge}>
              <Text style={styles.rankText}>중요도 {issue.topicRank}</Text>
            </View>
          </View>

          <View style={styles.keywordsContainer}>
            {issue.keywords?.map((keyword, idx) => (
              <Text key={idx} style={styles.keywordBadge}>
                {keyword}
              </Text>
            ))}
          </View>

          <Text style={styles.issueContent}>{issue.content}</Text>
        </View>

        <View style={styles.relatedNewsContainer}>
          <Text style={styles.sectionTitle}>관련 뉴스</Text>

          {loading ? (
            <View style={styles.loadingContainer}>
              <ActivityIndicator size="large" color="#176FF2" />
              <Text style={styles.loadingText}>
                관련 뉴스를 로딩 중입니다...
              </Text>
            </View>
          ) : relatedNews.length > 0 ? (
            relatedNews.map((newsItem, index) => (
              <TouchableOpacity
                key={index}
                style={styles.newsItem}
                onPress={() => {
                  navigation.navigate("NewsDetail", {
                    id: newsItem.id,
                    title: newsItem.title,
                    url:
                      newsItem.url ||
                      `https://www.sedaily.com/News/Article/${newsItem.id}`,
                  });
                }}
              >
                <Text style={styles.newsTitle}>{newsItem.title}</Text>
                <Text style={styles.newsDescription}>
                  {newsItem.description}
                </Text>
                <View style={styles.newsMetaContainer}>
                  <Text style={styles.newsSource}>
                    {newsItem.source || "서울경제"}
                  </Text>
                  <Text style={styles.newsDate}>
                    {formatDate(newsItem.date)}
                  </Text>
                </View>
              </TouchableOpacity>
            ))
          ) : (
            <View style={styles.emptyContainer}>
              <Text style={styles.emptyText}>관련 뉴스가 없습니다.</Text>
            </View>
          )}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f9f9f9",
  },
  header: {
    flexDirection: "row",
    alignItems: "center",
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: "#eaeaea",
    backgroundColor: "#fff",
  },
  backButton: {
    marginRight: 16,
  },
  backButtonText: {
    color: "#176FF2",
    fontSize: 16,
  },
  headerTitle: {
    fontSize: 18,
    fontWeight: "bold",
    color: "#232323",
  },
  issueContainer: {
    backgroundColor: "#fff",
    padding: 20,
    margin: 16,
    borderRadius: 12,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 2,
  },
  issueTitle: {
    fontSize: 20,
    fontWeight: "bold",
    color: "#232323",
    marginBottom: 16,
    lineHeight: 28,
  },
  issueMetaContainer: {
    flexDirection: "row",
    marginBottom: 16,
  },
  rankBadge: {
    backgroundColor: "#176FF2",
    paddingHorizontal: 12,
    paddingVertical: 6,
    borderRadius: 20,
  },
  rankText: {
    fontSize: 14,
    color: "#ffffff",
    fontWeight: "600",
  },
  keywordsContainer: {
    flexDirection: "row",
    flexWrap: "wrap",
    marginBottom: 16,
  },
  keywordBadge: {
    backgroundColor: "#E8F0FE",
    paddingHorizontal: 10,
    paddingVertical: 5,
    borderRadius: 16,
    marginRight: 8,
    marginBottom: 8,
    fontSize: 13,
    color: "#176FF2",
    fontWeight: "500",
  },
  issueContent: {
    fontSize: 16,
    color: "#333",
    lineHeight: 24,
  },
  relatedNewsContainer: {
    padding: 16,
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: "bold",
    color: "#232323",
    marginBottom: 16,
  },
  loadingContainer: {
    padding: 20,
    alignItems: "center",
  },
  loadingText: {
    marginTop: 8,
    color: "#666",
  },
  newsItem: {
    backgroundColor: "#fff",
    padding: 16,
    borderRadius: 8,
    marginBottom: 12,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.05,
    shadowRadius: 2,
    elevation: 1,
  },
  newsTitle: {
    fontSize: 16,
    fontWeight: "bold",
    color: "#232323",
    marginBottom: 8,
    lineHeight: 22,
  },
  newsDescription: {
    fontSize: 14,
    color: "#5F5F5F",
    marginBottom: 12,
    lineHeight: 20,
  },
  newsMetaContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  newsSource: {
    fontSize: 12,
    color: "#176FF2",
  },
  newsDate: {
    fontSize: 12,
    color: "#888",
  },
  emptyContainer: {
    padding: 20,
    alignItems: "center",
    backgroundColor: "#fff",
    borderRadius: 8,
  },
  emptyText: {
    color: "#888",
    fontSize: 16,
  },
});

export default IssueTimeline;
