import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  ActivityIndicator,
  StyleSheet,
  TouchableOpacity,
  Share,
  SafeAreaView,
  ScrollView,
  Dimensions,
} from "react-native";
import { RouteProp, useNavigation } from "@react-navigation/native";
import { StackNavigationProp } from "@react-navigation/stack";
import { RootStackParamList } from "../types/navigation";
import { Ionicons } from "@expo/vector-icons";

type StoryResultScreenRouteProp = RouteProp<RootStackParamList, "StoryResult">;
type StoryResultScreenNavigationProp = StackNavigationProp<
  RootStackParamList,
  "StoryResult"
>;

type Props = {
  route: StoryResultScreenRouteProp;
};

// 기사 아이템 타입 정의
interface NewsItem {
  id: string;
  title: string;
  date: string;
  summary: string;
  context: string;
  link: string;
}

// 화면 너비 계산
const SCREEN_WIDTH = Dimensions.get("window").width;

const StoryResultScreen: React.FC<Props> = ({ route }) => {
  const { keyword } = route.params;
  const navigation = useNavigation<StoryResultScreenNavigationProp>();

  const [loading, setLoading] = useState(true);
  const [newsStory, setNewsStory] = useState<NewsItem[]>([]);
  const [activeIndex, setActiveIndex] = useState(0);

  // 데이터 가져오기 (모의 데이터 사용)
  useEffect(() => {
    // 실제 API 호출 대신 모의 데이터 생성
    setTimeout(() => {
      const mockData: NewsItem[] = [
        {
          id: "1",
          title: `${keyword}에 관한 첫 번째 뉴스`,
          date: "2023-08-10",
          summary:
            "이 기사는 해당 키워드에 대한 첫 번째 뉴스 내용을 요약합니다.",
          context:
            "이 뉴스는 해당 스토리의 시작점으로, 주요 사건의 발단을 다루고 있습니다.",
          link: "https://example.com/news/1",
        },
        {
          id: "2",
          title: `${keyword} 관련 동향 분석`,
          date: "2023-08-11",
          summary: "업계 전문가들의 동향 분석과 전망을 담은 기사입니다.",
          context:
            "첫 번째 뉴스 이후 전문가들의 분석이 나오며, 이후 진행될 방향성을 제시합니다.",
          link: "https://example.com/news/2",
        },
        {
          id: "3",
          title: `${keyword}가 경제에 미치는 영향`,
          date: "2023-08-12",
          summary: "해당 이슈가 국내외 경제에 미치는 다양한 영향을 분석합니다.",
          context:
            "이슈의 경제적 파급력이 드러나기 시작하며, 다양한 분야로 확산되고 있습니다.",
          link: "https://example.com/news/3",
        },
        {
          id: "4",
          title: `정부, ${keyword} 관련 대책 발표`,
          date: "2023-08-14",
          summary: "정부가 해당 이슈에 대한 공식 입장과 대응책을 발표했습니다.",
          context:
            "사회적 관심이 커지자 정부 차원의 개입이 시작되었고, 상황이 새로운 국면으로 접어들었습니다.",
          link: "https://example.com/news/4",
        },
        {
          id: "5",
          title: `${keyword} 관련 최신 동향 및 전망`,
          date: "2023-08-16",
          summary:
            "해당 이슈의 최신 상황과 앞으로의 전망을 종합적으로 분석합니다.",
          context:
            "지금까지의 흐름을 종합하고, 앞으로의 전개를 전망하며 스토리가 마무리됩니다.",
          link: "https://example.com/news/5",
        },
      ];
      setNewsStory(mockData);
      setLoading(false);
    }, 1500); // 로딩 시뮬레이션
  }, [keyword]);

  // 뉴스 상세 화면으로 이동
  const handleNewsPress = (item: NewsItem) => {
    navigation.navigate("NewsDetail", {
      url: item.link,
      title: item.title,
    });
  };

  // 공유 기능
  const shareStory = async () => {
    try {
      const currentNews = newsStory[activeIndex];
      await Share.share({
        message: `${currentNews.title}\n\n${currentNews.summary}\n\n${currentNews.link}`,
        title: `스토리 in 서울경제: ${keyword}`,
      });
    } catch (error) {
      console.error("공유하는데 실패했습니다:", error);
    }
  };

  // 페이지 변경 처리
  const handleScroll = (event: any) => {
    const contentOffset = event.nativeEvent.contentOffset;
    const pageIndex = Math.round(contentOffset.x / SCREEN_WIDTH);
    if (pageIndex !== activeIndex) {
      setActiveIndex(pageIndex);
    }
  };

  // 로딩 중 표시
  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#176FF2" />
        <Text style={[styles.loadingText, { fontFamily: undefined }]}>
          스토리를 만들고 있습니다...
        </Text>
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView
        horizontal
        pagingEnabled
        showsHorizontalScrollIndicator={false}
        onMomentumScrollEnd={handleScroll}
        style={styles.scrollView}
      >
        {newsStory.map((item, index) => (
          <View
            key={item.id}
            style={[styles.cardContainer, { width: SCREEN_WIDTH }]}
          >
            <View style={styles.card}>
              <View style={styles.cardHeader}>
                <Text style={[styles.cardDate, { fontFamily: undefined }]}>
                  {item.date}
                </Text>
                <Text
                  style={[
                    styles.cardNumber,
                    { fontFamily: undefined, fontWeight: "bold" },
                  ]}
                >
                  {index + 1}/{newsStory.length}
                </Text>
              </View>

              <Text
                style={[
                  styles.cardTitle,
                  { fontFamily: undefined, fontWeight: "bold" },
                ]}
              >
                {item.title}
              </Text>

              <View style={styles.divider} />

              <Text
                style={[
                  styles.summaryTitle,
                  { fontFamily: undefined, fontWeight: "bold" },
                ]}
              >
                요약
              </Text>
              <Text style={[styles.summaryText, { fontFamily: undefined }]}>
                {item.summary}
              </Text>

              <Text
                style={[
                  styles.contextTitle,
                  { fontFamily: undefined, fontWeight: "bold" },
                ]}
              >
                맥락
              </Text>
              <Text
                style={[
                  styles.contextText,
                  { fontFamily: undefined, fontStyle: "italic" },
                ]}
              >
                {item.context}
              </Text>

              <View style={styles.cardFooter}>
                <TouchableOpacity
                  style={styles.cardButton}
                  onPress={() => handleNewsPress(item)}
                >
                  <Text
                    style={[
                      styles.cardButtonText,
                      { fontFamily: undefined, fontWeight: "bold" },
                    ]}
                  >
                    자세히 보기
                  </Text>
                </TouchableOpacity>

                <TouchableOpacity
                  style={styles.shareButton}
                  onPress={shareStory}
                >
                  <Ionicons name="share-outline" size={24} color="#176FF2" />
                </TouchableOpacity>
              </View>
            </View>
          </View>
        ))}
      </ScrollView>

      {/* 페이지 인디케이터 */}
      <View style={styles.pageIndicatorContainer}>
        {newsStory.map((_, index) => (
          <View
            key={index}
            style={[
              styles.pageIndicator,
              index === activeIndex && styles.activePageIndicator,
            ]}
          />
        ))}
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f5f5f5",
  },
  loadingContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#fff",
  },
  loadingText: {
    marginTop: 16,
    fontSize: 16,
    color: "#666",
    fontFamily: "CircularXX-Medium",
  },
  scrollView: {
    flex: 1,
  },
  cardContainer: {
    padding: 15,
  },
  card: {
    backgroundColor: "#fff",
    borderRadius: 16,
    padding: 20,
    marginBottom: 10,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
    minHeight: 400,
  },
  cardHeader: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginBottom: 12,
  },
  cardDate: {
    color: "#666",
    fontSize: 14,
    fontFamily: "CircularXX-Regular",
  },
  cardNumber: {
    color: "#176FF2",
    fontSize: 14,
    fontWeight: "bold",
    fontFamily: "CircularXX-Bold",
  },
  cardTitle: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: 16,
    lineHeight: 28,
    fontFamily: "Montserrat-Bold",
  },
  divider: {
    height: 1,
    backgroundColor: "#eee",
    marginVertical: 16,
  },
  summaryTitle: {
    fontSize: 16,
    fontWeight: "bold",
    color: "#333",
    marginBottom: 8,
    fontFamily: "Montserrat-SemiBold",
  },
  summaryText: {
    fontSize: 16,
    color: "#333",
    lineHeight: 24,
    marginBottom: 16,
    fontFamily: "CircularXX-Regular",
  },
  contextTitle: {
    fontSize: 16,
    fontWeight: "bold",
    color: "#176FF2",
    marginBottom: 8,
    fontFamily: "Montserrat-SemiBold",
  },
  contextText: {
    fontSize: 16,
    color: "#666",
    lineHeight: 24,
    marginBottom: 16,
    fontStyle: "italic",
    fontFamily: "CircularXX-Regular",
  },
  cardFooter: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginTop: "auto",
  },
  cardButton: {
    backgroundColor: "#176FF2",
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 8,
  },
  cardButtonText: {
    color: "#fff",
    fontWeight: "bold",
    fontFamily: "CircularXX-Bold",
  },
  shareButton: {
    padding: 8,
  },
  pageIndicatorContainer: {
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    paddingVertical: 10,
  },
  pageIndicator: {
    width: 8,
    height: 8,
    borderRadius: 4,
    backgroundColor: "#ccc",
    marginHorizontal: 4,
  },
  activePageIndicator: {
    backgroundColor: "#176FF2",
    width: 12,
    height: 12,
    borderRadius: 6,
  },
});

export default StoryResultScreen;
