// src/screens/HomeScreen.tsx

import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  TouchableOpacity,
  SafeAreaView,
  ScrollView,
  FlatList,
  ActivityIndicator,
  Dimensions,
} from "react-native";
import { useNavigation } from "@react-navigation/native";
import { StackNavigationProp } from "@react-navigation/stack";
import { RootStackParamList } from "../types/navigation";
import { getTodayIssues, IssueItem } from "../services/newsService";
import Carousel from "react-native-snap-carousel";

// 화면 너비 가져오기
const { width: screenWidth } = Dimensions.get("window");

// 간단한 날짜 포맷 함수
const formatDate = (date: Date): string => {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  return `${year}-${month}-${day}`;
};

type HomeScreenNavigationProp = StackNavigationProp<RootStackParamList, "Home">;

const HomeScreen = () => {
  const navigation = useNavigation<HomeScreenNavigationProp>();
  const [searchKeyword, setSearchKeyword] = useState("");
  const [todayIssues, setTodayIssues] = useState<IssueItem[]>([]);
  const [isIssuesLoading, setIsIssuesLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [activeSlide, setActiveSlide] = useState(0);

  useEffect(() => {
    loadTodayIssues();
  }, [selectedDate]);

  const loadTodayIssues = async () => {
    try {
      setIsIssuesLoading(true);
      const formattedDate = formatDate(selectedDate);
      const issues = await getTodayIssues(formattedDate);
      setTodayIssues(issues);
    } catch (error) {
      console.error("오늘의 이슈 로드 오류:", error);
    } finally {
      setIsIssuesLoading(false);
    }
  };

  const handleSearch = () => {
    if (searchKeyword.trim()) {
      navigation.navigate("StoryResult", { keyword: searchKeyword });
    }
  };

  const handleDateSelect = () => {
    navigation.navigate("DatePicker");
  };

  const renderIssueCard = ({ item }: { item: IssueItem }) => (
    <TouchableOpacity
      style={styles.issueCard}
      onPress={() => navigateToIssueTimeline(item)}
    >
      <View style={styles.issueCardContent}>
        <Text style={styles.issueTitle} numberOfLines={2}>
          {item.title || "이슈 제목"}
        </Text>
        <View style={styles.issueTagsContainer}>
          {item.keywords.slice(0, 3).map((keyword, index) => (
            <View key={index} style={styles.issueTag}>
              <Text style={styles.issueTagText}>#{keyword}</Text>
            </View>
          ))}
        </View>
        <Text style={styles.issueDate}>{formatDate(selectedDate)}</Text>
        <Text style={styles.issueNewsCount}>
          관련 기사 {item.newsIds.length}개
        </Text>
      </View>
    </TouchableOpacity>
  );

  const navigateToIssueTimeline = (issue: IssueItem) => {
    navigation.navigate("IssueTimeline", {
      issue: issue,
      keyword: issue.keywords[0] || issue.title,
    });
  };

  const renderPagination = () => {
    return (
      <View style={styles.paginationContainer}>
        {todayIssues.map((_, index) => (
          <View
            key={index}
            style={[
              styles.paginationDot,
              {
                backgroundColor: index === activeSlide ? "#176FF2" : "#D9D9D9",
              },
            ]}
          />
        ))}
      </View>
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView>
        <View style={styles.header}>
          <Text
            style={[
              styles.title,
              { fontFamily: undefined, fontWeight: "bold" },
            ]}
          >
            스토리 in 서울경제
          </Text>
          <TouchableOpacity
            onPress={handleDateSelect}
            style={styles.datePickerButton}
          >
            <Text style={styles.dateText}>{formatDate(selectedDate)}</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.searchContainer}>
          <TextInput
            style={[styles.searchInput, { fontFamily: undefined }]}
            placeholder="키워드를 입력하세요"
            value={searchKeyword}
            onChangeText={setSearchKeyword}
            onSubmitEditing={handleSearch}
          />
          <TouchableOpacity style={styles.searchButton} onPress={handleSearch}>
            <Text
              style={[
                styles.searchButtonText,
                { fontFamily: undefined, fontWeight: "bold" },
              ]}
            >
              검색
            </Text>
          </TouchableOpacity>
        </View>

        <View style={styles.issuesContainer}>
          <Text
            style={[
              styles.issuesTitle,
              { fontFamily: undefined, fontWeight: "600" },
            ]}
          >
            {formatDate(selectedDate)}의 주요 이슈
          </Text>

          {isIssuesLoading ? (
            <ActivityIndicator
              size="large"
              color="#176FF2"
              style={styles.loader}
            />
          ) : todayIssues.length > 0 ? (
            <View style={styles.carouselContainer}>
              <Carousel
                data={todayIssues}
                renderItem={renderIssueCard}
                sliderWidth={screenWidth}
                itemWidth={screenWidth - 60}
                onSnapToItem={(index) => setActiveSlide(index)}
                activeSlideAlignment="center"
                inactiveSlideScale={0.95}
                inactiveSlideOpacity={0.7}
                containerCustomStyle={styles.carousel}
              />
              {renderPagination()}
            </View>
          ) : (
            <View style={styles.noIssuesContainer}>
              <Text style={styles.noIssuesText}>이슈가 없습니다</Text>
            </View>
          )}
        </View>

        <View style={styles.recommendContainer}>
          <Text
            style={[
              styles.recommendTitle,
              { fontFamily: undefined, fontWeight: "600" },
            ]}
          >
            추천 키워드
          </Text>
          <View style={styles.keywordContainer}>
            {["경제", "주식", "부동산", "금융", "기업", "국제"].map(
              (keyword, index) => (
                <TouchableOpacity
                  key={index}
                  style={styles.keywordButton}
                  onPress={() => {
                    setSearchKeyword(keyword);
                    navigation.navigate("StoryResult", {
                      keyword,
                      options: {
                        sortBy: "date", // 최신순 정렬
                      },
                    });
                  }}
                >
                  <Text style={[styles.keywordText, { fontFamily: undefined }]}>
                    {keyword}
                  </Text>
                </TouchableOpacity>
              )
            )}
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f9f9f9",
  },
  header: {
    padding: 20,
    alignItems: "center",
    flexDirection: "column",
  },
  title: {
    fontSize: 24,
    marginBottom: 8,
    color: "#176FF2",
  },
  datePickerButton: {
    backgroundColor: "#E8F1FD",
    paddingHorizontal: 15,
    paddingVertical: 8,
    borderRadius: 20,
  },
  dateText: {
    color: "#176FF2",
    fontWeight: "500",
  },
  searchContainer: {
    flexDirection: "row",
    paddingHorizontal: 20,
    marginBottom: 20,
  },
  searchInput: {
    flex: 1,
    height: 50,
    backgroundColor: "#ffffff",
    borderRadius: 8,
    paddingHorizontal: 15,
    marginRight: 10,
    borderWidth: 1,
    borderColor: "#eaeaea",
  },
  searchButton: {
    backgroundColor: "#176FF2",
    borderRadius: 8,
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 20,
  },
  searchButtonText: {
    color: "#ffffff",
    fontSize: 16,
  },
  recommendContainer: {
    padding: 20,
  },
  recommendTitle: {
    fontSize: 18,
    marginBottom: 15,
    color: "#232323",
  },
  keywordContainer: {
    flexDirection: "row",
    flexWrap: "wrap",
  },
  keywordButton: {
    backgroundColor: "rgba(23,111,242,0.1)",
    paddingHorizontal: 15,
    paddingVertical: 8,
    borderRadius: 20,
    marginRight: 10,
    marginBottom: 10,
  },
  keywordText: {
    color: "#176FF2",
  },
  issuesContainer: {
    padding: 20,
  },
  issuesTitle: {
    fontSize: 18,
    marginBottom: 15,
    color: "#232323",
  },
  carouselContainer: {
    alignItems: "center",
  },
  carousel: {
    flexGrow: 0,
  },
  issueCard: {
    backgroundColor: "#ffffff",
    borderRadius: 12,
    padding: 20,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 2,
    height: 200,
  },
  issueCardContent: {
    flex: 1,
    justifyContent: "space-between",
  },
  issueTitle: {
    fontSize: 18,
    fontWeight: "bold",
    color: "#232323",
    marginBottom: 10,
  },
  issueTagsContainer: {
    flexDirection: "row",
    flexWrap: "wrap",
    marginBottom: 10,
  },
  issueTag: {
    backgroundColor: "rgba(23,111,242,0.1)",
    paddingHorizontal: 10,
    paddingVertical: 5,
    borderRadius: 15,
    marginRight: 8,
    marginBottom: 5,
  },
  issueTagText: {
    color: "#176FF2",
    fontSize: 12,
  },
  issueDate: {
    fontSize: 12,
    color: "#5F5F5F",
    marginBottom: 5,
  },
  issueNewsCount: {
    fontSize: 12,
    color: "#5F5F5F",
  },
  paginationContainer: {
    flexDirection: "row",
    justifyContent: "center",
    marginTop: 15,
  },
  paginationDot: {
    width: 8,
    height: 8,
    borderRadius: 4,
    marginHorizontal: 4,
  },
  loader: {
    marginVertical: 50,
  },
  noIssuesContainer: {
    height: 200,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#ffffff",
    borderRadius: 12,
  },
  noIssuesText: {
    color: "#5F5F5F",
    fontSize: 16,
  },
});

export default HomeScreen;
