import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Text,
  ActivityIndicator,
  SafeAreaView,
  ScrollView,
  Share,
  TouchableOpacity,
} from "react-native";
import { useRoute, RouteProp } from "@react-navigation/native";
import { Ionicons } from "@expo/vector-icons";
import { RootStackParamList } from "../types/navigation";

type NewsDetailScreenRouteProp = RouteProp<RootStackParamList, "NewsDetail">;

const NewsDetailScreen = () => {
  const route = useRoute<NewsDetailScreenRouteProp>();
  const { url, title } = route.params;

  const [loading, setLoading] = useState(true);
  const [newsContent, setNewsContent] = useState("");

  useEffect(() => {
    // 실제 API 연동 시 주석 해제
    // fetch(url)
    //   .then(response => response.text())
    //   .then(data => {
    //     // HTML 파싱 및 콘텐츠 추출 로직 필요
    //     setNewsContent(extractedContent);
    //     setLoading(false);
    //   })
    //   .catch(error => {
    //     console.error(error);
    //     setLoading(false);
    //   });

    // 임시 콘텐츠로 대체
    setTimeout(() => {
      setNewsContent(`서울경제신문은 국내 최고의 경제지로서 빠르고 정확한 정보를 제공합니다.

${title}

최근 발표된 경제지표에 따르면 올해 경제성장률은 예상보다 높을 것으로 전망됩니다. 전문가들은 현재 시장 상황이 장기 투자자들에게 좋은 기회라고 말합니다.

정부가 새로운 경제 활성화 정책을 발표했습니다. 이번 정책은 특히 중소기업에 혜택이 많습니다. 새 정책의 핵심은 세금 감면과 금융 지원 확대입니다.

전문가들은 "이번 정책이 경기 회복에 도움이 될 것"이라며 긍정적인 반응을 보이고 있습니다. 다만 일부에서는 정책의 지속가능성에 대한 우려도 제기하고 있습니다.

한편, 주요 기업들은 올해 실적 개선을 전망하고 있으며, 특히 IT와 바이오 섹터가 강세를 보일 것으로 예상됩니다.

더 많은 경제 뉴스는 서울경제신문 웹사이트에서 확인하실 수 있습니다.`);
      setLoading(false);
    }, 1500);
  }, [url, title]);

  const handleShare = async () => {
    try {
      await Share.share({
        message: `${title}\n\n${url}`,
        title: title,
      });
    } catch (error) {
      console.error(error);
    }
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#176FF2" />
        <Text style={styles.loadingText}>콘텐츠를 불러오는 중...</Text>
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.contentContainer}>
        <Text style={styles.newsTitle}>{title}</Text>

        <View style={styles.metaContainer}>
          <Text style={styles.dateText}>발행일: 2023-07-15</Text>
          <TouchableOpacity onPress={handleShare} style={styles.shareButton}>
            <Ionicons name="share-outline" size={20} color="#176FF2" />
            <Text style={styles.shareText}>공유</Text>
          </TouchableOpacity>
        </View>

        <View style={styles.divider} />

        <Text style={styles.contentText}>{newsContent}</Text>

        <TouchableOpacity
          style={styles.sourceButton}
          onPress={() => {
            // 원본 기사로 이동하는 로직 필요
            // Linking.openURL(url);
          }}
        >
          <Text style={styles.sourceButtonText}>원본 기사 보기</Text>
        </TouchableOpacity>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  loadingContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  loadingText: {
    marginTop: 10,
    fontFamily: "CircularXX-Medium",
    color: "#5F5F5F",
  },
  contentContainer: {
    padding: 20,
  },
  newsTitle: {
    fontFamily: "Montserrat-Bold",
    fontSize: 22,
    color: "#232323",
    marginBottom: 15,
    lineHeight: 30,
  },
  metaContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: 15,
  },
  dateText: {
    fontFamily: "CircularXX-Regular",
    fontSize: 12,
    color: "#B8B8B8",
  },
  shareButton: {
    flexDirection: "row",
    alignItems: "center",
  },
  shareText: {
    fontFamily: "CircularXX-Medium",
    fontSize: 14,
    color: "#176FF2",
    marginLeft: 5,
  },
  divider: {
    height: 1,
    backgroundColor: "#F0F0F0",
    marginBottom: 20,
  },
  contentText: {
    fontFamily: "CircularXX-Regular",
    fontSize: 16,
    color: "#3A3A3A",
    lineHeight: 24,
    marginBottom: 30,
  },
  sourceButton: {
    alignSelf: "center",
    backgroundColor: "#176FF2",
    paddingHorizontal: 20,
    paddingVertical: 12,
    borderRadius: 8,
    marginBottom: 30,
  },
  sourceButtonText: {
    fontFamily: "CircularXX-Bold",
    fontSize: 14,
    color: "#FFFFFF",
  },
});

export default NewsDetailScreen;
