import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  ActivityIndicator,
  SafeAreaView,
  Linking,
  Image,
} from "react-native";
import { RouteProp } from "@react-navigation/native";
import { StackNavigationProp } from "@react-navigation/stack";
import { RootStackParamList } from "../types/navigation";
import {
  searchNewsByKeyword,
  IssueItem,
  NewsItem,
  getNewsDetailsByIds,
  isSeoulEconomic,
  generateBigkindsUrl,
  getProviderFromNewsId,
} from "../services/newsService";

type IssueTimelineRouteProp = RouteProp<RootStackParamList, "IssueTimeline">;
type IssueTimelineNavigationProp = StackNavigationProp<
  RootStackParamList,
  "IssueTimeline"
>;

type Props = {
  route: IssueTimelineRouteProp;
  navigation: IssueTimelineNavigationProp;
};

// 언론사별 뉴스 통계를 저장하는 타입 정의
interface ProviderStat {
  name: string;
  count: number;
  code: string;
}

const IssueTimeline = ({ route, navigation }: Props) => {
  const { issue, keyword } = route.params;
  const [relatedNews, setRelatedNews] = useState<NewsItem[]>([]);
  const [seoulEconomicNews, setSeoulEconomicNews] = useState<NewsItem[]>([]);
  const [providerStats, setProviderStats] = useState<ProviderStat[]>([]);
  const [loading, setLoading] = useState(true);
  const [statsLoading, setStatsLoading] = useState(true);

  useEffect(() => {
    fetchNewsCluster();
  }, [issue]);

  // 이슈의 news_cluster를 사용하여 관련 뉴스 가져오기
  const fetchNewsCluster = async () => {
    try {
      setLoading(true);
      setStatsLoading(true);

      if (!issue.newsClusterIds || issue.newsClusterIds.length === 0) {
        // news_cluster가 없는 경우 키워드 검색으로 대체
        await fetchByKeyword();
        return;
      }

      console.log(
        `뉴스 클러스터 ID ${issue.newsClusterIds.length}개 처리 중...`
      );

      // 언론사별 통계 계산
      const providerCounts: Record<
        string,
        { name: string; count: number; code: string }
      > = {};
      for (const newsId of issue.newsClusterIds) {
        const providerCode = newsId.split(".")[0];
        const providerName = getProviderFromNewsId(newsId);

        if (!providerCounts[providerCode]) {
          providerCounts[providerCode] = {
            name: providerName,
            count: 0,
            code: providerCode,
          };
        }
        providerCounts[providerCode].count++;
      }

      // 통계 데이터 정렬 (기사 수 기준 내림차순)
      const stats = Object.values(providerCounts).sort(
        (a, b) => b.count - a.count
      );
      setProviderStats(stats);
      setStatsLoading(false);

      // 서울경제 뉴스 ID 분리
      const seoulEconomicIds = issue.newsClusterIds.filter((id) =>
        isSeoulEconomic(id)
      );

      // 최대 5개의 서울경제 뉴스 가져오기
      if (seoulEconomicIds.length > 0) {
        const seoulNews = await getNewsDetailsByIds(
          seoulEconomicIds.slice(0, 5)
        );
        setSeoulEconomicNews(seoulNews);
      }

      // 다른 언론사의 뉴스도 일부 가져오기 (최대 10개)
      // 주요 언론사 기준으로 각 언론사별 1-2개씩 균형있게 가져오기
      const topProviders = stats.slice(0, 5);
      const otherNewsIds: string[] = [];

      for (const provider of topProviders) {
        if (provider.code === "02100311") continue; // 서울경제는 이미 처리함

        // 해당 언론사의 뉴스 ID 찾기
        const providerNewsIds = issue.newsClusterIds.filter((id) =>
          id.startsWith(provider.code)
        );

        // 각 언론사에서 최대 2개씩 추가
        otherNewsIds.push(...providerNewsIds.slice(0, 2));

        if (otherNewsIds.length >= 10) break;
      }

      if (otherNewsIds.length > 0) {
        const otherNews = await getNewsDetailsByIds(otherNewsIds);
        setRelatedNews(otherNews);
      } else {
        setRelatedNews([]);
      }
    } catch (error) {
      console.error("뉴스 클러스터 처리 중 오류:", error);
      // 오류 발생 시 키워드 검색으로 대체
      await fetchByKeyword();
    } finally {
      setLoading(false);
    }
  };

  // 기존의 키워드 검색 방식 (fallback)
  const fetchByKeyword = async () => {
    try {
      // 이슈와 관련된 뉴스 검색 (키워드 배열에서 첫 번째 키워드 사용)
      const primaryKeyword =
        issue.keywords && issue.keywords.length > 0
          ? issue.keywords[0]
          : keyword;

      const newsResults = await searchNewsByKeyword(primaryKeyword, 10);
      setRelatedNews(newsResults);

      // 서울경제 뉴스 필터링
      const seoulNews = newsResults.filter(
        (news) => news.source === "서울경제" || news.id.startsWith("02100311")
      );
      setSeoulEconomicNews(seoulNews);

      // 간단한 통계 생성
      const providerCounts: Record<string, number> = {};
      for (const news of newsResults) {
        const provider = news.source || "알 수 없음";
        providerCounts[provider] = (providerCounts[provider] || 0) + 1;
      }

      // 통계 변환 및 정렬
      const stats: ProviderStat[] = Object.entries(providerCounts)
        .map(([name, count]) => ({
          name,
          count,
          code: name === "서울경제" ? "02100311" : "",
        }))
        .sort((a, b) => b.count - a.count);

      setProviderStats(stats);
      setStatsLoading(false);
    } catch (error) {
      console.error("관련 뉴스 로딩 오류:", error);
      setRelatedNews([]);
      setSeoulEconomicNews([]);
      setProviderStats([]);
      setStatsLoading(false);
    }
  };

  const formatDate = (dateString: string) => {
    if (!dateString) return "";

    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");

    return `${year}.${month}.${day}`;
  };

  const openNewsLink = (url: string) => {
    Linking.canOpenURL(url).then((supported) => {
      if (supported) {
        Linking.openURL(url);
      } else {
        console.log("Cannot open URL: " + url);
      }
    });
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView>
        <View style={styles.header}>
          <TouchableOpacity
            style={styles.backButton}
            onPress={() => navigation.goBack()}
          >
            <Text style={styles.backButtonText}>← 뒤로</Text>
          </TouchableOpacity>
          <Text style={styles.headerTitle}>이슈 상세정보</Text>
        </View>

        {/* 이슈 정보 */}
        <View style={styles.issueContainer}>
          <Text style={styles.issueTitle}>{issue.topic}</Text>

          <View style={styles.issueMetaContainer}>
            <View style={styles.rankBadge}>
              <Text style={styles.rankText}>중요도 {issue.topicRank}</Text>
            </View>
          </View>

          <View style={styles.keywordsContainer}>
            {issue.keywords?.map((keyword, idx) => (
              <Text key={idx} style={styles.keywordBadge}>
                {keyword}
              </Text>
            ))}
          </View>

          <Text style={styles.issueContent}>{issue.content}</Text>
        </View>

        {/* 관련 기사 통계 */}
        <View style={styles.statsContainer}>
          <Text style={styles.sectionTitle}>관련 기사 통계</Text>

          {statsLoading ? (
            <ActivityIndicator size="small" color="#176FF2" />
          ) : (
            <>
              <Text style={styles.statsSummary}>
                총{" "}
                <Text style={styles.highlight}>
                  {issue.newsClusterIds?.length || 0}개
                </Text>
                의 관련 기사
              </Text>

              <Text style={styles.statsSectionTitle}>언론사별 보도 현황</Text>
              <View style={styles.statsGrid}>
                {providerStats.slice(0, 8).map((stat, idx) => (
                  <View key={idx} style={styles.statItem}>
                    <Text style={styles.statName}>{stat.name}</Text>
                    <Text style={styles.statCount}>{stat.count}건</Text>
                  </View>
                ))}
              </View>

              {providerStats.length > 8 && (
                <Text style={styles.statsMore}>
                  외 {providerStats.length - 8}개 언론사
                </Text>
              )}
            </>
          )}
        </View>

        {/* 서울경제 이야기 */}
        <View style={styles.seContainer}>
          <Text style={styles.sectionTitle}>서울경제 이야기</Text>

          {loading ? (
            <View style={styles.loadingContainer}>
              <ActivityIndicator size="large" color="#176FF2" />
              <Text style={styles.loadingText}>
                서울경제 뉴스를 로딩 중입니다...
              </Text>
            </View>
          ) : seoulEconomicNews.length > 0 ? (
            seoulEconomicNews.map((newsItem, index) => (
              <TouchableOpacity
                key={index}
                style={styles.seNewsItem}
                onPress={() => {
                  navigation.navigate("NewsDetail", {
                    id: newsItem.id,
                    title: newsItem.title,
                    url: newsItem.url,
                  });
                }}
              >
                {newsItem.imageUrl && (
                  <Image
                    source={{ uri: newsItem.imageUrl }}
                    style={styles.newsImage}
                    resizeMode="cover"
                  />
                )}
                <Text style={styles.seNewsTitle}>{newsItem.title}</Text>
                <Text style={styles.seNewsDescription}>{newsItem.content}</Text>
                <View style={styles.newsMetaContainer}>
                  <Text style={styles.newsSource}>서울경제</Text>
                  <Text style={styles.newsDate}>
                    {formatDate(newsItem.date)}
                  </Text>
                </View>
              </TouchableOpacity>
            ))
          ) : (
            <View style={styles.emptyContainer}>
              <Text style={styles.emptyText}>
                서울경제 관련 뉴스가 없습니다.
              </Text>
            </View>
          )}
        </View>

        {/* 다른 언론사 뉴스 */}
        <View style={styles.relatedNewsContainer}>
          <Text style={styles.sectionTitle}>타 언론사 주요 기사</Text>

          {loading ? (
            <View style={styles.loadingContainer}>
              <ActivityIndicator size="large" color="#176FF2" />
              <Text style={styles.loadingText}>
                관련 뉴스를 로딩 중입니다...
              </Text>
            </View>
          ) : relatedNews.length > 0 ? (
            relatedNews.map((newsItem, index) => (
              <TouchableOpacity
                key={index}
                style={styles.newsItem}
                onPress={() => openNewsLink(newsItem.url)}
              >
                <Text style={styles.newsTitle}>{newsItem.title}</Text>
                <Text style={styles.newsDescription}>{newsItem.content}</Text>
                <View style={styles.newsMetaContainer}>
                  <Text style={styles.newsSource}>
                    {newsItem.provider || "언론사 정보 없음"}
                  </Text>
                  <Text style={styles.newsDate}>
                    {formatDate(newsItem.date)}
                  </Text>
                </View>
              </TouchableOpacity>
            ))
          ) : (
            <View style={styles.emptyContainer}>
              <Text style={styles.emptyText}>관련 뉴스가 없습니다.</Text>
            </View>
          )}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

